mapper.update:
update ncomment
set content = #{content}
where cno = #{cno}

mapper.select:
select reply
from ncomment
where cno=#{cno}

select count(cno)
from ncomment
where cno != #{cno} and grp = #{cno}

select count(cno)
from ncomment
where cno != #{grp} and grp = #{grp}

mapper.delete:
delete from ncomment


		where grp = #{grp} and nno = #{nno}


		where cno = #{cno} and nno = #{nno}
