mapper.select:
select * from notice 

	 where id in

		#{it}


order by nno desc
limit #{sno}, #{rowsPerPage}

select count(*) from notice

	 where id in

		#{it}

select title, uploadfile, n.id, nname, regdate, cnt, content, img, nno
from notice n, user u
where n.id = u.id and nno=#{nno}

mapper.update:
update notice 
set title=#{title}, content=#{content}, subject=#{subject}, img=#{img} 
where nno=#{nno}
