mapper.select:
select title, subject, uploadfile, n.id, nname, DATE_FORMAT(regdate, '%Y-%m-%d') regdate, cnt, content, img, rvno
from review n, user u
where n.id = u.id and rvno=#{rvno}

mapper.update:
update review 
set title=#{title}, content=#{content}, subject=#{subject}, img=#{img} 
where rvno=#{rvno}

update review_reply
set content = #{content}
where rrno = #{rrno}

mapper.delete:
delete from review_reply


		where grp = #{grp} and rvno = #{rvno}


		where rrno = #{rrno}
